mapper.select:
SELECT storeIdx
From Partner
WHERE partnerIdx = #{partnerIdx}

SELECT storeIdx
FROM Reservations
WHERE reservationIdx = #{calculationIdx}

mapper.update:
UPDATE Reservations
SET calculateStatus = 2
WHERE reservationIdx = #{calculationIdx}

